mapper.select:
SELECT * FROM mishu.message_push

    is_send = 1

        AND userId = #{userid}


        AND is_system = #{isSystem}


ORDER BY id DESC

SELECT m.*, r.readOrReaded
FROM mishu.message_push m
LEFT JOIN mishu_write.readed_record r ON m.id = r.msg_id

    m.is_send = 1

        AND m.userId = #{userid} AND r.userId = #{userid}


        AND m.is_system = #{isSystem}


        AND r.readOrReaded = #{readOrReaded}


ORDER BY m.id DESC

SELECT COUNT(*) FROM mishu.message_push m LEFT JOIN mishu_write.readed_record AS r ON m.id = r.msg_id
m.is_send = 1
AND m.userId = #{userid}
AND m.is_system = #{isSystem}
AND r.readOrReaded = 0
ORDER BY m.id DESC

SELECT id
FROM mishu.message_push

    is_send = 1

        AND userId = #{userid}


        AND is_system = #{isSystem}
